// Obtener la URL desde los datos entrantes
const url = $json.link_youtube;

// Funci√≥n para extraer el ID del video de YouTube
function getYouTubeVideoID(url) {
    const regex = /[?&]v=([a-zA-Z0-9_-]{11})/;
    const match = url.match(regex);
    return match ? match[1] : null;
}

// Extraer el ID del video
const videoID = getYouTubeVideoID(url);

// Devolver el ID como resultado
return { videoID, url };

// Obtener el HTML
const htmlContent = $input.first().json["data"];


// Extraer todos los t√≠tulos y p√°rrafos usando expresiones regulares
const titles = [];
const paragraphs = [];

// Extraer todos los t√≠tulos (h1, h2, h3, etc.)
const titleMatches = htmlContent.match(/<h[1-6][^>]*>(.*?)<\/h[1-6]>/g);
if (titleMatches) {
    for (let match of titleMatches) {
        titles.push(match.replace(/<[^>]+>/g, '')); // Eliminar etiquetas HTML
    }
}

// Extraer todos los p√°rrafos
const paragraphMatches = htmlContent.match(/<p[^>]*>(.*?)<\/p>/g);
if (paragraphMatches) {
    for (let match of paragraphMatches) {
        paragraphs.push(match.replace(/<[^>]+>/g, '')); // Eliminar etiquetas HTML
    }
}

// Unir todos los t√≠tulos y p√°rrafos en un solo texto
const content = "T√≠tulos:\n" + titles.join("\n") + "\n\nP√°rrafos:\n" + paragraphs.join(" ");

// Limitar el n√∫mero de caracteres para OpenAI (opcional)
const maxCharacters = 10000;
let finalContent = content;
if (finalContent.length > maxCharacters) {
    finalContent = finalContent.slice(0, maxCharacters) + '...';
}

// Retornar solo el contenido procesado y reducido
return [{ json: { content: finalContent, url :  $('Switch').item.json.URL  } }];

