/**
 * Extrae el invite code de una URL de invitación de WhatsApp.
 * @param {string} url - La URL de invitación (p. ej. https://chat.whatsapp.com/ABCDE12345?mode=ac_t)
 * @returns {string|null} - El código de invitación, o null si no coincide.
 */
function extractInviteCode(url) {
  const regex = /https?:\/\/chat\.whatsapp\.com\/([^?]+)/;
  const match = url.match(regex);
  return match ? match[1] : null;
}

// Ejemplo de uso
const url = $('Link Grupo').first().json.link_grupo;
const inviteCode = extractInviteCode(url);
return {id: inviteCode}

