// Valor original (puede venir de tu input)
let numero = $input.first().json.properties.hs_calculated_phone_number.value;

// Asegúrate de que el valor es una cadena
numero = String(numero);

// Quitar el "+"
numero = numero.replace('+', '');

// Verificar que empieza con "52" y agregar "1" después
if (numero.startsWith('52')) {
  numero = '52' + '1' + numero.slice(2);
}

return [{ json: { telefono: numero } }];

const raw = $input.first().json.output;

// Reemplaza los "\n" (literales) por saltos de línea reales
const cleanText = raw.replace(/\\n/g, "\n");

// Devuelve como JSON para usar en el siguiente nodo
return [
  {
    json: {
      message: cleanText
    }
  }
];

